import { createSlice } from '@reduxjs/toolkit';

const authSlice = createSlice({

    name: "auth",

    initialState: {
        isLoggedIn: false,
        userData: null,
        userTransactionHistory : null
    },

    reducers:{
        login: (state, action) => {
            state.isLoggedIn = true;
            state.userData = action.payload.userData || state.userData;
            state.userTransactionHistory = action.payload.transaction_history || state.userTransactionHistory;
            //console.log("AuthSlice");
            //console.log("state.userTransactionHistory: ", action.payload.transaction_history);
            console.log("State: ", state);
            console.log("Action: ", action);
            // state.userData = action.payload.userData 
            // state.userTransactionHistory = action.payload.transaction_history
            
        },
        logout: (state) => {

            state.isLoggedIn = false;
            state.userData = null;
            state.userTransactionHistory = null;
        }
    }
});

export default authSlice.reducer;
export const login = authSlice.actions.login;
export const logout = authSlice.actions.logout;
