import { createSlice } from '@reduxjs/toolkit';

const authSlice = createSlice({

    name: "auth",

    initialState: {
        isLoggedIn: false,
        userData: null,
        userTransactionHistory : null
    },

    reducers:{
        login: (state, action) => {
            state.isLoggedIn = true;
            
            //console.log("This is dumb");
            //console.log(action.payload.userTransactionHistory)
            //if (action.payload.userTransactionHistory) {
            state.userTransactionHistory = action.payload.userTransactionHistory;
            //}
            
        },
        logout: (state) => {

            state.isLoggedIn = false;
            state.userData = null;
            state.userTransactionHistory = null;
        }
    }
});

export default authSlice.reducer;
export const login = authSlice.actions.login;
export const logout = authSlice.actions.logout;
